import React from 'react';
import Select from 'react-select';

// hat-tip:
// https://appdividend.com/2018/10/19/react-dropdown-select-example-tutorial/
// https://stackoverflow.com/questions/43495696/how-to-set-a-default-value-in-react-select

const charts = [
  { label: "Age & Income", value: 1 },
  { label: "Houses & Income", value: 2 },
  { label: "Houses & Population Density", value: 3 },
  { label: "LFL Density & Population Density", value: 4 },
  { label: "% Visible Minorities & Non-official Languages", value: 5 },
];

const Dropdown = () => (
  <div className="dropdown">
    <Select
        options={ charts }
        autosize={ false }
        defaultValue={{ label: "Age & Income", value: 1 }}
        onChange={e => {
              this.setState({
              chart: e.label,
              chartId: e.value
              });
           }}
    />
  </div>
);

export default Dropdown
