import { ResponsiveScatterPlot } from '@nivo/scatterplot';
import data from "../data/data.json";


const Chart = () => {
    return (
        <div className="chart">
            <ResponsiveScatterPlot
                data={data}
                margin={{ top: 60, right: 140, bottom: 100, left: 90 }}
                xScale={{ type: 'linear', min: 0, max: 'auto' }}
                xFormat=">-.2f"
                yScale={{ type: 'linear', min: 0, max: 'auto' }}
                yFormat=">-.2f"
                // https://stackoverflow.com/questions/67241395/how-to-add-custom-color-theme-in-nivo-chart-settings
                // /*
                colors={['#0707FF',
                        '#3737FF',
                        '#B7B7FF',
                        '#D1D1FF',
                        '#D9D9FF',
                        '#DBDBFF',
                        '#DCDCFF',
                        '#E0E0FF',
                        '#E3E3FF',
                        '#E4E4FF',
                        '#ECECFF',
                        '#EFEFFF',
                        '#F0F0FF',
                        '#F1F1FF',
                        '#F4F4FF',]}
                // */
                // Follows this order for LFL #s:
                        // Victoria
                        // Saanich
                        // Oak Bay
                        // Esquimalt
                        // North Saanich
                        // Langford
                        // Central Saanich
                        // Colwood
                        // View Royal
                        // Sooke
                        // Metchosin
                        // Sidney
                        // Highlands
                        // Juan de Fuca (Part 1)
                        // Juan de Fuca (Part 2)
                // Same order but LFL/SqKm
                /*
                colors={['#1515FF',
                    '#D3D3FF',
                    '#A2A2FF',
                    '#B5B5FF',
                    '#E9E9FF',
                    '#E9E9FF',
                    '#EBEBFF',
                    '#E1E1FF',
                    '#DFDFFF',
                    '#F0F0FF',
                    '#F3F3FF',
                    '#E4E4FF',
                    '#F3F3FF',
                    '#F5F5FF',
                    '#F5F5FF']}
                
                */
                
                colorBy="index"
                blendMode="multiply"
                axisTop={null}
                axisRight={null}
                axisBottom={{
                    orient: 'bottom',
                    tickSize: 5,
                    tickPadding: 5,
                    tickRotation: 0,
                    legend: 'weight',
                    legendPosition: 'middle',
                    legendOffset: 46
                }}
                onClick={(data) => {
                    console.log(`x: ${data["x"]}`);
                }}
                axisLeft={{
                    orient: 'left',
                    tickSize: 5,
                    tickPadding: 5,
                    tickRotation: 0,
                    legend: 'size',
                    legendPosition: 'middle',
                    legendOffset: -60
                }}
                legends={[
                    {
                        anchor: 'right',
                        direction: 'row',
                        justify: false,
                        translateX: 130,
                        translateY: 80,
                        itemWidth: 100,
                        itemHeight: 24,
                        itemsSpacing: 5,
                        itemDirection: 'left-to-right',
                        symbolSize: 12,
                        symbolShape: 'square',
                        effects: [
                            {
                                on: 'hover',
                                style: {
                                    itemOpacity: 1
                                }
                            }
                        ]
                    }
                ]}
            />
        </div>
    )
};

export default Chart;
