import React from 'react';
import Select from 'react-select';

// hat-tip:
// https://appdividend.com/2018/10/19/react-dropdown-select-example-tutorial/
// https://stackoverflow.com/questions/43495696/how-to-set-a-default-value-in-react-select

const chart_select = [
  { label: "Age & Income", chart_seleced: 1 },
  { label: "Houses & Income", chart_selected: 2 },
  { label: "Houses & Population Density", chart_selected: 3 },
  { label: "LFL Density & Population Density", chart_selected: 4 },
  { label: "% Visible Minorities & Non-official Languages", chart_selected: 5 }
];

const legend_select = [
  { label: "# of LFLs", legend_selected: 1 },
  { label: "LFLs / SqKm", legend_selected: 2 },
];

export default class Dropdown extends React.Component {
    render() {
    return (
            <div className = "sideBySide">
            <div className="dropdown">
                <div style={{width: '40%'}}>
                <Select
                    options={ chart_select }
                    autosize={ false }
                    defaultValue={{ label: "Age & Income", chart_selected: 1 }}
                />
                </div>
                <div style={{width: '20%'}}>
                <Select
                    options={ legend_select }
                    autosize={ false }
                    defaultValue={{ label: "# of LFLs", legend_selected: 1 }}
                />
                </div>
            </div>
            </div>
        )
    }
}

// export default Dropdown
