import React from 'react';
import Select from 'react-select';

// hat-tip:
// https://appdividend.com/2018/10/19/react-dropdown-select-example-tutorial/
// https://stackoverflow.com/questions/43495696/how-to-set-a-default-value-in-react-select

const chart = [
  { label: "Age & Income", seleced: 1 },
  { label: "Houses & Income", selected: 2 },
  { label: "Houses & Population Density", selected: 3 },
  { label: "LFL Density & Population Density", selected: 4 },
  { label: "% Visible Minorities & Non-official Languages", selected: 5 }
];

export default class Dropdown extends React.Component {
    setSelected(ev) {
        debugger;
        this.props.onDropdownSelect(ev.selected);
    }
    render() {
    return (
            <div className="dropdown">
                <div style={{width: '50%'}}>
                <Select
                    options={ chart }
                    autosize={ false }
                    defaultValue={{ label: "Age & Income", selected: 1 }}
                    onChange={this.setSelected.bind(this)}
                />
                </div>
          </div>
        )
    }
}

// export default Dropdown
