import React from "react";
import ReactDOM from "react-dom";

import Title from "./components/Title.js";
import Chart from "./components/Chart.js";
import Map from "./components/Map.js";
import Dropdown from "./Dropdown/Map.js";

import "./index.css";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      value: 1,
      map: null,
      x: "",
      y: ""
    };

    this.onChartElementClick = this.onChartElementClick.bind(this);

    this.onMapCreate = this.onMapCreate.bind(this);

    this.onDropdownSelection = this.onDropdownSelection.bind(this);
  }

  onChartElementClick(val) {
    this.setState({
      value: val,
      map: this.state.map
    });
  }

  onMapCreate(map) {
    this.setState({
      value: this.state.value,
      map: map
    });
  }

  onDropdownSelection(x, y) {
    this.setState({
      value: this.state.value,
      map: this.state.map,
      x: x,
      y: y
    });
  }

  render() {
    return (
      <div className="app">
        <Title />
        <Dropdown onDropdownSelection={this.onDropdownSelection} />
        <div className="sideBySide">
          <Chart value={this.state} onChartElementClick={this.onChartElementClick} />
          <Map state={this.state} onMapCreate={this.onMapCreate}/>
        </div>
      </div>
    );
  }
}

ReactDOM.render(
  <React.Fragment>
    <App />
  </React.Fragment>,
  document.getElementById("root")
);
