import React from 'react';
import Select from 'react-select';

// hat-tip:
// https://appdividend.com/2018/10/19/react-dropdown-select-example-tutorial/
// https://stackoverflow.com/questions/43495696/how-to-set-a-default-value-in-react-select

const legend = [
  { label: "Colour: Total # LFLs", selected: 1 },
  { label: "Colour: LFLs/SqKm", selected: 2 },
];

export default class Dropdown extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value2: legend[0]
        }
    }

    setColoured(ev) {
        this.props.onDropdownSelection(ev.selected); 
        this.setState({
            value: legend[ev.selected-1] 
        })
    }

    render() {
    return (
            <div className="dropdown">
                <div style={{width: '50%'}}>
                <Select
                    options={ legend }
                    autosize={ false }
                    value={ this.state.value2 }
                    getOptionValue={(option) => option.label} 
                    onChange={this.setSelected.bind(this)}
                />
                </div>
          </div>
        )
    }
}

// export default Dropdown
