import React from 'react';
import Select from 'react-select';

// credit: https://appdividend.com/2018/10/19/react-dropdown-select-example-tutorial/ for the code base and instruction

const techCompanies = [
  { label: "Age & Income", value: 1 },
  { label: "Houses & Income", value: 2 },
  { label: "Netflix", value: 3 },
  { label: "Tesla", value: 4 },
  { label: "Amazon", value: 5 },
  { label: "Alphabet", value: 6 },
];

const Dropdown = () => (
  <div className="container">
    <div className="row">
      <div className="col-md-4"></div>
      <div className="col-md-4">
        <Select options={ techCompanies } />
      </div>
      <div className="col-md-4"></div>
    </div>
  </div>
);

export default Dropdown
